import { CONSTANTS } from "../../consts/ConstantVals.js";

/**
 * エクスポート
 */
export default {
  // props
  props: [],
  // data
  data() {
    return {
      // メニュー用の項目
      menuItems: [CONSTANTS.ROOM_FOOTER_MENU_ITEM_NAMES_BGM_STOP],
      // BGM再生中か
      isBgmPlaying: true
    };
  },
  //======================================================
  //
  // 1. setup
  //
  //======================================================

  /**
   * setup
   */
  mounted() {
    try {
      this.imageFiles = this.$store.getters.getBgImages;

      // storeにデータをセット
      this.$store.commit("setIsBgmPlaying", this.isBgmPlaying);
    } catch (error) {
      console.error(error);
    }
  },
  //======================================================
  //
  // 2. methods
  //
  //======================================================

  methods: {
    //======================================================
    //
    // 2-1. リストのアイテムクリック
    //
    //======================================================

    /**
     * リストのアイテムクリック
     */
    onListItemClick(item, i) {
      try {
        console.log(item, i);
        if (item.title === "BGM停止") {
          this.isBgmPlaying = false;
          this.menuItems = [CONSTANTS.ROOM_FOOTER_MENU_ITEM_NAMES_BGM_PLAY];
        } else {
          this.isBgmPlaying = true;
          this.menuItems = [CONSTANTS.ROOM_FOOTER_MENU_ITEM_NAMES_BGM_STOP];
        }

        // storeにデータをセット
        this.$store.commit("setIsBgmPlaying", this.isBgmPlaying);
      } catch (error) {
        console.error(error);
      }
    },
    //======================================================
    //
    // 2-2. OKボタンクリック
    //
    //======================================================

    /**
     * OKボタンクリック
     *   ・ダイアログを閉じる
     *   ・storeに選択したBGMをセット
     */
    onOKButtonClick() {
      try {
        console.log();
      } catch (error) {
        console.error(error);
      }
    }
  }
};
